import { useIsFormDirty, useIsFormValid } from 'vee-validate';
import { computed } from 'vue';
export default {
  setup() {
    const isDirty = useIsFormDirty();
    const isValid = useIsFormValid();
    const isDisabled = computed(() => {
      return !isDirty.value || !isValid.value;
    });
    return {
      isDisabled
    };
  }
};
